    <a class="navbar-brand px-4" href="#">Management System</a>
    <div class="navbar-collapse">
      <ul class="navbar-nav me-auto">
        <li class="nav item dropdown">
          <a class="nav-link dropdown-toggle" role="button" data-bs-toggle="dropdown">
          Employee
          </a> 
          <ul class="dropdown-menu dropdown-menu-dark">
            <li><a *ngIf="getUserRole() === '1'" class="dropdown-item" routerLinkActive="active"routerLink="./addemployee">Add Employee</a></li> 
            <li><a class="dropdown-item" routerLinkActive="active"routerLink="./showemployee">Show Employee</a></li>
          </ul>
        </li>
      </ul>
    </div>
    <li class="nav item dropdown">
      <a class="nav-link dropdown-toggle" role="button" data-bs-toggle="dropdown">
        Log Out
      </a> 
      <ul class="dropdown-menu dropdown-menu-dark">
        <li><a class="dropdown-item" routerLinkActive="active" (click)="logout()">Logout</a></li> 
        <button *ngIf="currentRole === 'admin'" (click)="switchRole()">Switch to User Mode</button>
        <button *ngIf="currentRole === 'user'" (click)="switchRole()">Switch to Admin Mode</button>
        <li><a  class="dropdown-item"class="dropdown-item" routerLinkActive="active"routerLink="../users">Swith To User</a></li>
      </ul>
    </li>
</nav>
