  import { ReturnStatement } from '@angular/compiler';
  import { Component,OnInit } from '@angular/core';
  import{FormGroup,FormControl,Validators}from'@angular/forms';
  import{RegistrationService}from'../../app/registration.service';
  import { hashSync,compareSync } from 'bcryptjs';
import { last } from 'rxjs';
  @Component({

    selector: 'app-registration',
    templateUrl: './registration.component.html',
    styleUrls: ['./registration.component.css']
  })
  export class RegistrationComponent {
    constructor(private api:RegistrationService
      ){}
    registerForm:any;
    users:any;
    data:any;
    user_data:any;


    ngOnInit():void{
      this.api.apiCall().subscribe((data)=>{
        this.users = data;
      })
    this.registerForm=new FormGroup({
      "firstname":new FormControl(null,[Validators.required,Validators.pattern('[a-zA-Z]*')]),
      "lastname":new FormControl(null,[Validators.required,Validators.pattern('[a-zA-Z]*')]),
      "email":new FormControl(null,[Validators.required,Validators.pattern('^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$')]),
      "password":new FormControl(null,[Validators.required,Validators.pattern('^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[$@$!%*?&])[A-Za-z\d$@$!%*?&].{8,}$')]),
      "password_confirmation":new FormControl(null,[Validators.required]),
    });
    console.log(this.users);
    }
    submitData()
    {
      const hashedPassword = hashSync(this.registerForm.value.password, 10);
      const formData = {
        ...this.registerForm.value,
        password: hashedPassword,
        password_confirmation: hashedPassword
      } 
      const id = this.user_data ? this.user_data.customer.id : null;
      if (id) {
        // If the ID exists, update the record
        /* this.api.editUser(id, formData).subscribe(data => {
          console.log(data);
        }); */
        alert(`User with ID ${id} has been updated`);
        this.user_data.customer.firstname = this.registerForm.value.firstname;
        this.user_data.customer.lastname = this.registerForm.value.lastname;
        this.user_data.customer.email = this.registerForm.value.email;
      } else {
        // If the ID does not exist, create a new record
        this.api.submitData(formData).subscribe(data => {
          console.log(data);
        });
        alert(`New user has been created`);
      }
      if(this.confirmpassword.value!=this.passWord.value){
        alert("password does not match");
        return;
      }
      if(this.registerForm.valid){
        alert(`Thank You ${this.registerForm.value.firstname}`);
      }
    }
     editUser(id:number,data:any){
      this.api.editUser(id,data).subscribe((data: any) => {
        this.user_data = data;
        console.log(this.user_data.customer);
        this.registerForm.patchValue({
          firstname:this.user_data.customer.firstname,
          lastname:this.user_data.customer.lastname,
          email:this.user_data.customer.email,
        })
      });
    } 
    deleteUser(id: number) {
      this.api.deleteUser(id).subscribe((data) => {
        console.log('User deleted successfully');
      });
    }
    get firstName(){  
      return this.registerForm.get('firstname');
    }
    get lastName(){
      return this.registerForm.get('lastname');
    }
    get emailid(){
      return this.registerForm.get('email');
    }
    get passWord(){
      return this.registerForm.get('password');
    }
    get confirmpassword(){
      return this.registerForm.get('password_confirmation');
    }
  }
<div class="continer">
    <form [formGroup]="registerForm" class="content" (ngSubmit)="submitData()">
        <h3 class="text-center">Registration Form</h3>
        <div class="row">
    <div class="mt-2">
        <input type="text" formControlName="firstname"class="form-control" placeholder="First name">
    <div *ngIf="firstName.invalid && (firstName.touched||firstName.dirty)">
        <span class="error" *ngIf="firstName.errors?.required">First name is required</span>
        <span class="error" *ngIf="firstName.errors?.pattern">Enter Only Characters</span>
    </div>
    </div>
    <div class="mt-2">
        <input type="text" formControlName="lastname"class="form-control" placeholder="Last name">
        <div *ngIf="lastName.invalid && (lastName.touched||lastName.dirty)">
        <span class="error" *ngIf="lastName.errors?.required">Lastname is required</span>
        <span class="error" *ngIf="lastName.errors?.pattern">Enter Only Characters</span>
    </div>
    </div>
    <div class="mt-2">
        <input type="text" formControlName="email" class="form-control" placeholder="Email-id">
        <div *ngIf="emailid.invalid && (emailid.touched||emailid.dirty)">
            <span class="error" *ngIf="emailid.errors?.required">Emailid is required</span>
            <span class="error" *ngIf="emailid.errors?.pattern">Enter Valid Email Address</span>
        </div>
    </div>
    <div class="mt-2">
        <input type="password" formControlName="password" cd class="form-control" placeholder="Password">
        <div *ngIf="passWord.invalid && (passWord.touched||passWord.dirty)">
            <span class="error" *ngIf="passWord.errors?.required">Password is required</span>
            <span class="error" *ngIf="passWord.errors?.pattern">Password must contain more than 8 characters, 1 numeric, 1 upper case letter, and 1 special character($@$!%*?&).</span>
        </div>
    </div>
    <div class="mt-2"> 
        <input type="password" formControlName="password_confirmation" cd class="form-control" placeholder="Confirm Password">
        <div *ngIf="confirmpassword.invalid && (confirmpassword.touched||confirmpassword.dirty)">
            <span class="error" *ngIf="confirmpassword.errors?.required">Confirm Password is required</span>
            <span class="error" *ngIf="confirmpassword.valid">The entered Password not match</span>
        </div>
    </div>
    </div>
    <div class="text-center">
    <div class="mt-3 col-lg-12 col-12">
        <button class="btn" [disabled]="registerForm.invalid">Submit</button>
    </div>
    </div>
    </form>
    </div>
